import React from "react";
import { useNavigate } from "react-router-dom";

const IndexPage = () => {
  const navigate = useNavigate();

  const handleLoginClick = () => {
    navigate("/login");
  };

  return (
    <div className="landing-container bg-gradient-to-r from-blue-500 via-indigo-600 to-purple-700 min-h-screen flex flex-col justify-center items-center text-white">
      <h1 className="text-4xl md:text-6xl font-bold mb-6 text-center">Welcome to Our SaaS Platform</h1>
      <p className="text-lg md:text-xl mb-8 text-center max-w-2xl">
        Unlock the full potential of your projects with our intuitive and powerful tools. Let's make things easier together!
      </p>
      <button
        onClick={handleLoginClick}
        className="bg-white text-blue-600 hover:text-blue-700 font-semibold px-8 py-4 rounded-2xl shadow-lg hover:shadow-xl transform hover:scale-105 transition duration-300"
      >
        Get Started
      </button>
    </div>
  );
};

export default IndexPage;
