// src/layout/Layout.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';  // To decode the JWT token
import './Layout.css';

function Layout({ children }) {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if a token exists in localStorage
    const token = localStorage.getItem("authToken");

    if (token) {
      try {
        // Decode the token to extract user data
        const decodedToken = jwt_decode(token);
        const userFirstName = decodedToken.firstname;  // Extract the first name
        setUser({ firstname: userFirstName });
      } catch (error) {
        console.error("Error decoding token:", error);
        // If token is invalid or expired, clear it from localStorage and redirect to login
        localStorage.removeItem("authToken");
        navigate("/login");
      }
    } else {
      // If no token, redirect to the login page
      navigate("/login");
    }
  }, [navigate]);

  return (
    <div className="layout-container">
      <div className="sidebar">
        {/* Sidebar content */}
        <ul>
          <li><a href="/dashboard">Dashboard</a></li>
          <li><a href="/analytics">Analytics</a></li>
          <li><a href="/settings">Settings</a></li>
        </ul>
      </div>
      <div className="main-content">
        <header>
          <div className="profile">
            {user ? (
              <span>{user.firstname}</span>  // Show user's first name
            ) : (
              <span>Loading...</span>
            )}
            <img src="profile.jpg" alt="Profile" />
            <div className="dropdown">
              <ul>
                <li><a href="/profile">Profile</a></li>
                <li><a href="/logout">Logout</a></li>
              </ul>
            </div>
          </div>
        </header>
        <div className="content">{children}</div>
      </div>
    </div>
  );
}

export default Layout;
