import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./RegisterPage.css";
import SpeechBubble from "../components/SpeechBubble";

const baseURL = (() => {
  const { hostname } = window.location;
  if (hostname === "127.0.0.1" || hostname === "localhost") {
    return "http://127.0.0.1:5000";
  } else if (hostname.includes("johnnygardner.co.uk")) {
    return "https://www.johnnygardner.co.uk";
  } else {
    return "https://saas-application.onrender.com";
  }
})();

const countries = ["United Kingdom", "United States", "Canada", "Australia", "Germany", "France", "India", "Japan"];

function RegisterPage() {
  const [step, setStep] = useState(1);
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [error, setError] = useState("");
  const [country, setCountry] = useState("");
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const [allergies, setAllergies] = useState([]);
  const [dietaryPreferences, setDietaryPreferences] = useState([]);
  const [days, setDays] = useState(1);
  const [selectedOptions, setSelectedOptions] = useState({
    allergies: [],
    dietaryPreferences: [],
  });

  const navigate = useNavigate();

  const options = [
    { type: "allergy", name: "Peanuts", emoji: "ðŸ¥œ" },
    { type: "allergy", name: "Tree Nuts", emoji: "ðŸŒ°" },
    { type: "allergy", name: "Dairy", emoji: "ðŸ¥›" },
    { type: "allergy", name: "Eggs", emoji: "ðŸ³" },
    { type: "allergy", name: "Shellfish", emoji: "ðŸ¦" },
    { type: "allergy", name: "Fish", emoji: "ðŸŸ" },
    { type: "allergy", name: "Wheat", emoji: "ðŸŒ¾" },
    { type: "allergy", name: "Gluten", emoji: "ðŸž" },
    { type: "allergy", name: "Soy", emoji: "ðŸŒ±" },
    { type: "allergy", name: "Sesame", emoji: "âšª" },
    { type: "dietary", name: "Vegetarian", emoji: "ðŸ¥—" },
    { type: "dietary", name: "Vegan", emoji: "ðŸŒ±" },
    { type: "dietary", name: "Keto", emoji: "ðŸ¥“" },
    { type: "dietary", name: "High-Protein", emoji: "ðŸ—" },
    { type: "dietary", name: "Pescatarian", emoji: "ðŸŸ" },
    { type: "dietary", name: "Low-Carb", emoji: "ðŸ¥©" },
    { type: "dietary", name: "Take-Out Style", emoji: "ðŸ±" },
  ];

  const handleCheckboxChange = (category, value) => {
    setSelectedOptions((prevState) => {
      const newCategory = prevState[category].includes(value)
        ? prevState[category].filter((item) => item !== value)
        : [...prevState[category], value];

      return {
        ...prevState,
        [category]: newCategory,
      };
    });
  };

  const renderCheckboxGroup = (type) => (
    <div className="step-container checkbox-group">
      <div className="speech-bubble-container">
        <SpeechBubble text={`Please select your ${type}:`} />
      </div>
      <div className="checkbox-wrapper">
        {options
          .filter((option) => option.type === type)
          .map((option) => (
            <label key={option.name} className="checkbox-label">
              <input
                type="checkbox"
                value={option.name}
                checked={selectedOptions[type].includes(option.name)}
                onChange={() => handleCheckboxChange(type, option.name)}
              />
              <span className="emoji">{option.emoji}</span> {option.name}
            </label>
          ))}
      </div>
    </div>
  );

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch(`${baseURL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          firstname,
          lastname,
          email,
          password,
          country,
          adults,
          children,
          allergies,
          dietaryPreferences,
          days,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("authToken", data.token);
        navigate("/dashboard");
      } else {
        const errorData = await response.json();
        setError(errorData.error || "Registration failed.");
      }
    } catch (err) {
      setError("Network error. Please try again.");
    }
  };

  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  const progress = (step - 1) * 16.67;

  return (
    <>
      <div className="progress-bar-container">
        {step > 1 && (
          <div className="back-arrow-container">
            <button className="back-arrow-button" onClick={handleBack}>
              <svg
                viewBox="0 0 44 44"
                focusable="false"
                className="back-arrow"
                data-testid="back-arrow"
              >
                <path
                  fillRule="evenodd"
                  clipRule="evenodd"
                  d="M17 21.9571L24.9571 29.9142L26.3713 28.5L19.8284 21.9571L26.3713 15.4142L24.9571 14L17 21.9571Z"
                  fill="currentColor"
                ></path>
              </svg>
            </button>
          </div>
        )}
        <div className="progress-container">
          <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        </div>
      </div>

      <div className="register-container">
        <div className="form-content">
          {step === 1 && (
            <div className="step-container">
              <div className="speech-bubble-container">
                <SpeechBubble text="Please select a country:" />
              </div>
              <select
                className="input-field select-field"
                value={country}
                onChange={(e) => setCountry(e.target.value)}
                required
              >
                <option value="">Select your country</option>
                {countries.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>
          )}

          {step === 2 && (
            <div className="step-container">
              <div className="speech-bubble-container">
                <SpeechBubble text="Please select family size:" />
              </div>
              <div className="family-size-group">
                <label>Adults:</label>
                <div className="number-input">
                  <button onClick={() => setAdults(adults > 1 ? adults - 1 : 1)}>-</button>
                  <input
                    type="number"
                    value={adults}
                    onChange={(e) => setAdults(Math.max(1, e.target.value))}
                    min="1"
                    required
                  />
                  <button onClick={() => setAdults(adults + 1)}>+</button>
                </div>
              </div>

              <div className="family-size-group">
                <label>Children:</label>
                <div className="number-input">
                  <button onClick={() => setChildren(children > 0 ? children - 1 : 0)}>-</button>
                  <input
                    type="number"
                    value={children}
                    onChange={(e) => setChildren(Math.max(0, e.target.value))}
                    min="0"
                    required
                  />
                  <button onClick={() => setChildren(children + 1)}>+</button>
                </div>
              </div>
            </div>
          )}

          {step === 3 && renderCheckboxGroup("allergies")}
          {step === 4 && renderCheckboxGroup("dietaryPreferences")}

          {step === 5 && (
            <div className="step-container">
              <div className="speech-bubble-container">
                <SpeechBubble text="How many days per week would you like meal plans for?" />
              </div>
              <div className="number-input">
                <button onClick={() => setDays(Math.max(1, days - 1))}>-</button>
                <input
                  type="number"
                  value={days}
                  onChange={(e) => setDays(Math.max(1, e.target.value))}
                  min="1"
                  required
                />
                <button onClick={() => setDays(days + 1)}>+</button>
              </div>
            </div>
          )}

          {step === 6 && (
            <div className="step-container">
              <button className="register-button" onClick={handleRegister}>
                Register
              </button>
              {error && <p className="error-message">{error}</p>}
            </div>
          )}

          <div className="nav-buttons">
            {step < 6 && (
              <button className="next-button" onClick={() => setStep(step + 1)}>
                Next
              </button>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default RegisterPage;
