import React, { useState, useEffect } from "react";
import "./SpeechBubble.css"; // Import the CSS for styling

const SpeechBubble = ({ text }) => {
  const [displayText, setDisplayText] = useState("");

  // Animate the text letter by letter
  useEffect(() => {
    let i = 0;
    setDisplayText(""); // Reset text display when text changes
    const interval = setInterval(() => {
      setDisplayText((prevText) => prevText + text.charAt(i)); // Use charAt for exact letter placement
      i += 1;
      if (i === text.length) clearInterval(interval);
    }, 50); // Adjust the typing speed

    return () => clearInterval(interval);
  }, [text]); // Re-run if 'text' changes

  return (
    <div className="speech-bubble">
      <div className="bubble">
        <p>{displayText}</p>
      </div>
    </div>
  );
};

export default SpeechBubble;
