import React, { useState, useEffect } from "react";
import { NavLink, Outlet, useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import './Layout.css';

function Layout() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (token) {
      try {
        // Decode the JWT token
        const decodedToken = jwt_decode(token);
        const userFirstName = decodedToken.firstname;
        setUser({ firstname: userFirstName });
      } catch (error) {
        console.error("Error decoding token:", error);
        localStorage.removeItem("authToken");
        navigate("/login");
      }
    } else {
      navigate("/login");
    }
  }, [navigate]);

  return (
    <div className="layout-container">
      <div className="sidebar">
        {/* Navigation links inside sidebar */}
        <ul>
          <li>
            <NavLink to="/dashboard" activeClassName="active-link">Dashboard</NavLink>
          </li>
          <li>
            <NavLink to="/analytics" activeClassName="active-link">Analytics</NavLink>
          </li>
          <li>
            <NavLink to="/settings" activeClassName="active-link">Settings</NavLink>
          </li>
        </ul>
      </div>

      <div className="main-content">
        {/* Topbar with Profile Info */}
        <header className="topbar">
          <div className="topbar-left">
            <span>My SaaS App</span>
          </div>
          <div className="topbar-right">
            <div className="profile">
              {user ? (
                <span>{user.firstname}</span>
              ) : (
                <span>Loading...</span>
              )}
              <img src="profile.jpg" alt="Profile" />
              <div className="dropdown">
                <ul>
                  <li><a href="/profile">Profile</a></li>
                  <li><a href="/logout">Logout</a></li>
                </ul>
              </div>
            </div>
          </div>
        </header>

        {/* The Outlet component renders the child route's content */}
        <div className="content">
          <Outlet />
        </div>
      </div>
    </div>
  );
}

export default Layout;
