import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Define baseURL dynamically
const baseURL = (() => {
  const { hostname } = window.location;
  if (hostname === "127.0.0.1" || hostname === "localhost") {
    return "http://127.0.0.1:5000";
  } else if (hostname.includes("johnnygardner.co.uk")) {
    return "https://www.johnnygardner.co.uk";
  } else {
    return "https://saas-application.onrender.com";
  }
})();

const Profile = () => {
  const [profileData, setProfileData] = useState(null); // Holds profile data
  const [error, setError] = useState(null); // Handles errors
  const [loading, setLoading] = useState(true); // Loading state
  const [success, setSuccess] = useState(""); // Success message
  const navigate = useNavigate();

  // Fetch user profile data on component mount
  useEffect(() => {
    const fetchProfileData = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        navigate("/login"); // Redirect to login if there's no token
        return;
      }

      try {
        const response = await fetch(`${baseURL}/profile`, {
          headers: {
            Authorization: `Bearer ${token}`, // Send token in the request
          },
        });

        if (!response.ok) {
          throw new Error("Unauthorized");
        }

        const result = await response.json();
        setProfileData(result); // Populate form with user data
      } catch (error) {
        setError("You are not authorized to access this page.");
        navigate("/login"); // Redirect to login if unauthorized
      } finally {
        setLoading(false); // Stop loading spinner
      }
    };

    fetchProfileData();
  }, [navigate]);

  // Update form data as user types
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission to update profile
  const handleSave = async () => {
    const token = localStorage.getItem("authToken");

    try {
      const response = await fetch(`${baseURL}/profile`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(profileData),
      });

      if (!response.ok) {
        throw new Error("Failed to update profile");
      }

      const result = await response.json();
      setSuccess(result.message); // Display success message
      setError(null); // Clear any previous error
    } catch (error) {
      setError("Error updating profile. Please try again."); // Display error
      setSuccess(""); // Clear any previous success
    }
  };

  // Display loading spinner while fetching data
  if (loading) {
    return <div>Loading...</div>;
  }

  // Display error message if fetching failed
  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Profile</h1>
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form>
        <label>
          First Name:
          <input
            type="text"
            name="firstname"
            value={profileData?.firstname || ""}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Last Name:
          <input
            type="text"
            name="lastname"
            value={profileData?.lastname || ""}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Email (Read-only):
          <input
            type="text"
            name="email"
            value={profileData?.email || ""}
            readOnly
          />
        </label>
        <br />
        <br />
        <button type="button" onClick={handleSave}>
          Update
        </button>
      </form>
    </div>
  );
};

export default Profile;
