import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import "./Layout.css";

const Layout = () => {
  const [userFirstName, setUserFirstName] = useState(null);
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const closeDropdown = (e) => {
    if (!e.target.closest(".profile")) {
      setDropdownOpen(false);
    }
  };

  React.useEffect(() => {
    document.addEventListener("click", closeDropdown);
    return () => {
      document.removeEventListener("click", closeDropdown);
    };
  }, []);

  React.useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        // Assuming "firstname" is stored in the token
        setUserFirstName(decodedToken.firstname);
      } catch (error) {
        console.error("Error decoding token:", error);
        // Optionally handle errors like redirecting to login if decoding fails
      }
    }
  }, []);
  return (
    <div>
      <header className="header">
        <div className="logo">SaaS Template</div>
        <nav>
          <div className="profile" onClick={toggleDropdown}>
            <span>{userFirstName}</span>
            <img
              src="/images/profile_image.jpg"
              alt="Profile"
            />
            <div className={`dropdown ${isDropdownOpen ? "open" : ""}`}>
              <ul>
                <li><NavLink to="/profile">My Profile</NavLink></li>
                <li><NavLink to="/admin-portal">Admin Portal</NavLink></li>
                <li><NavLink to="/logout">Sign Out</NavLink></li>
              </ul>
            </div>
          </div>
        </nav>
      </header>
      <div className="container">
        <aside className="sidebar">
          <ul>
            <li><NavLink to="/dashboard">Dashboard</NavLink></li>
            <li><NavLink to="/analytics">Analytics</NavLink></li>
            <li><NavLink to="/settings">Settings</NavLink></li>
          </ul>
        </aside>
        <main className="content">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
