// Determine the base URL dynamically
const baseURL = (() => {
    const { hostname } = window.location;
    if (hostname === "127.0.0.1" || hostname === "localhost") {
        return "http://127.0.0.1:5000";
    } else if (hostname.includes("johnnygardner.co.uk")) {
        return "https://www.johnnygardner.co.uk";
    } else {
        return "https://saas-application.onrender.com";
    }
})();

// Move all imports to the top as per ESLint's "import/first" rule
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './RegisterPage.css'; // Use the same CSS for Register

function RegisterPage() {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const isEmailValid = email.match(/\S+@\S+\.\S+/);
  const isFormValid = firstname && lastname && email && password && passwordConfirm && isEmailValid && password === passwordConfirm;

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch(`${baseURL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ firstname, lastname, email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("authToken", data.token); // Save token
        navigate("/dashboard");
      } else {
        const errorData = await response.json();
        setError(errorData.error || "Registration failed.");
      }
    } catch (err) {
      setError("Network error. Please try again.");
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={handleRegister} id="register-form">
        <input
          type="text"
          className="input-field"
          placeholder="First Name"
          value={firstname}
          onChange={(e) => setFirstname(e.target.value)}
          required
        />
        <input
          type="text"
          className="input-field"
          placeholder="Last Name"
          value={lastname}
          onChange={(e) => setLastname(e.target.value)}
          required
        />
        <input
          type="email"
          className="input-field"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          className="input-field"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <input
          type="password"
          className="input-field"
          placeholder="Confirm Password"
          value={passwordConfirm}
          onChange={(e) => setPasswordConfirm(e.target.value)}
          required
        />
        <button type="submit" className="button" disabled={!isFormValid}>
          Register
        </button>
      </form>
      {error && <div id="error-message" className="error-message">{error}</div>}
      <div className="links">
        <a href="/login">Already have an account? Login</a>
      </div>
    </div>
  );
}

export default RegisterPage;
