import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./RegisterPage.css";

const baseURL = (() => {
    const { hostname } = window.location;
    if (hostname === "127.0.0.1" || hostname === "localhost") {
        return "http://127.0.0.1:5000";
    } else if (hostname.includes("johnnygardner.co.uk")) {
        return "https://www.johnnygardner.co.uk";
    } else {
        return "https://saas-application.onrender.com";
    }
})();

const countries = ["United Kingdom", "United States", "Canada", "Australia", "Germany", "France", "India", "Japan"];
const allergyOptions = ["Dairy", "Gluten", "Peanuts", "Shellfish", "Soy", "Tree Nuts", "Eggs", "Fish"];
const dietaryOptions = ["Vegetarian", "Vegan", "Keto", "High-Protein", "Pescatarian", "Low-Carb", "Take-Out Style"];

function RegisterPage() {
  const [step, setStep] = useState(1);
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [error, setError] = useState("");
  const [country, setCountry] = useState("");
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const [allergies, setAllergies] = useState([]);
  const [dietaryPreferences, setDietaryPreferences] = useState([]);
  const [days, setDays] = useState(1);
  const navigate = useNavigate();

  const handleCheckboxChange = (setFunction, values, value) => {
    setFunction(values.includes(value) ? values.filter(v => v !== value) : [...values, value]);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch(`${baseURL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ firstname, lastname, email, password, country, adults, children, allergies, dietaryPreferences, days }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("authToken", data.token);
        navigate("/dashboard");
      } else {
        const errorData = await response.json();
        setError(errorData.error || "Registration failed.");
      }
    } catch (err) {
      setError("Network error. Please try again.");
    }
  };

  const getStepTitle = () => {
    switch (step) {
      case 1:
        return "Select Your Country";
      case 2:
        return "Enter Family Details";
      case 3:
        return "Select Allergies";
      case 4:
        return "Select Dietary Preferences";
      case 5:
        return "Meal Plan Duration";
      case 6:
        return "Complete Your Information";
      default:
        return "Register";
    }
  };

  // Function to handle back navigation
  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  // Calculate progress percentage
  const progress = (step - 1) * 20;

  return (
    <div className="register-container">
      <h2>{getStepTitle()}</h2>

      {/* Back Button */}
      {step > 1 && (
        <button className="back-button" onClick={handleBack}>
          &lt;
        </button>
      )}

      {/* Progress Bar */}
      <div className="progress-container">
        <div className="progress-bar" style={{ width: `${(step - 1) * 16.67}%` }}></div>
      </div>

      {/* Step Content */}
      <div className="form-content">
        {/* Step 1: Country Selection */}
        {step === 1 && (
          <div className="step-container">
            <label>Country:</label>
            <select value={country} onChange={(e) => setCountry(e.target.value)} required>
              <option value="">Select your country</option>
              {countries.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>
        )}

        {/* Step 2: Family Details */}
        {step === 2 && (
          <div className="step-container">
            <label>Adults:</label>
            <div className="number-input">
              <button onClick={() => setAdults(adults > 1 ? adults - 1 : 1)}>-</button>
              <input type="number" value={adults} onChange={(e) => setAdults(Math.max(1, e.target.value))} min="1" required />
              <button onClick={() => setAdults(adults + 1)}>+</button>
            </div>
            <label>Children:</label>
            <div className="number-input">
              <button onClick={() => setChildren(children > 0 ? children - 1 : 0)}>-</button>
              <input type="number" value={children} onChange={(e) => setChildren(Math.max(0, e.target.value))} min="0" required />
              <button onClick={() => setChildren(children + 1)}>+</button>
            </div>
          </div>
        )}

        {/* Step 3: Allergies Selection */}
        {step === 3 && (
          <div className="step-container checkbox-group">
            <label>Allergies:</label>
            {allergyOptions.map((allergy) => (
              <label key={allergy}>
                <input
                  type="checkbox"
                  value={allergy}
                  checked={allergies.includes(allergy)}
                  onChange={() => handleCheckboxChange(setAllergies, allergies, allergy)}
                />
                <span></span>
                {allergy}
              </label>
            ))}
          </div>
        )}

        {/* Step 4: Dietary Preferences Selection */}
        {step === 4 && (
          <div className="step-container checkbox-group">
            <label>Dietary Preferences:</label>
            {dietaryOptions.map((diet) => (
              <label key={diet}>
                <input type="checkbox" value={diet} checked={dietaryPreferences.includes(diet)} onChange={() => handleCheckboxChange(setDietaryPreferences, dietaryPreferences, diet)} />
                {diet}
              </label>
            ))}
          </div>
        )}

        {/* Step 5: Meal Plan Duration */}
        {step === 5 && (
          <div className="step-container">
            <label>Number of Days for Meal Plan:</label>
            <div className="number-input">
              <button onClick={() => setDays(days > 1 ? days - 1 : 1)}>-</button>
              <input type="number" value={days} onChange={(e) => setDays(Math.max(1, e.target.value))} min="1" max="7" required />
              <button onClick={() => setDays(days + 1)}>+</button>
            </div>
          </div>
        )}

        {/* Step 6: User Information */}
        {step === 6 && (
          <form onSubmit={handleRegister} id="register-form" className="form-container">
            <input type="text" className="input-field" placeholder="First Name" value={firstname} onChange={(e) => setFirstname(e.target.value)} required />
            <input type="text" className="input-field" placeholder="Last Name" value={lastname} onChange={(e) => setLastname(e.target.value)} required />
            <input type="email" className="input-field" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            <input type="password" className="input-field" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            <input type="password" className="input-field" placeholder="Confirm Password" value={passwordConfirm} onChange={(e) => setPasswordConfirm(e.target.value)} required />
          </form>
        )}
      </div>

      {/* Next/Submit Button */}
      <div className="form-actions">
        {step !== 6 && (
          <button
            className="button-next"
            onClick={() => setStep(step + 1)}
          >
            Next
          </button>
        )}
        {step === 6 && (
          <button type="submit" className="button-next" onClick={handleRegister}>
            Register
          </button>
        )}
      </div>

      {/* Error Message */}
      {error && <div id="error-message" className="error-message">{error}</div>}

      {/* Links to Login Page */}
      <div className="links">
        <a href="/login">Already have an account? Login</a>
      </div>
    </div>
  );
}

export default RegisterPage;
