import React, { useState, useEffect } from "react";
import { useNavigate, useOutletContext  } from "react-router-dom";
import "../common.css"; // Shared styles
import "./ProfilePage.css"; // Profile-specific styles

const baseURL = (() => {
  const { hostname } = window.location;
  if (hostname === "127.0.0.1" || hostname === "localhost") {
    return "http://127.0.0.1:5000";
  } else if (hostname.includes("johnnygardner.co.uk")) {
    return "https://www.johnnygardner.co.uk";
  } else {
    return "https://saas-application.onrender.com";
  }
})();

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState("");
  const [deletionError, setDeletionError] = useState("");
  const [deletionSuccess, setDeletionSuccess] = useState("");
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const navigate = useNavigate();
  const { updateFirstName } = useOutletContext();

  useEffect(() => {
    const fetchProfileData = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const response = await fetch(`${baseURL}/profile`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Unauthorized");
        }

        const result = await response.json();
        setProfileData(result);
      } catch (error) {
        setError("You are not authorized to access this page.");
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };

    fetchProfileData();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    const token = localStorage.getItem("authToken");

    try {
      const response = await fetch(`${baseURL}/profile`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(profileData),
      });

      if (!response.ok) {
        throw new Error("Failed to update profile");
      }

      const result = await response.json();
      setSuccess(result.message);
      setError(null);
      // Update the user's first name in the Topbar
      if (profileData.firstname) {
        updateFirstName(profileData.firstname);
      }

    } catch (error) {
      setError("Error updating profile. Please try again.");
      setSuccess("");
    }
  };

  // Handle Account Deletion
  const handleDeleteAccount = async () => {
    const token = localStorage.getItem("authToken");

    try {
      const response = await fetch(`${baseURL}/delete_account`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete account");
      }

      // Success message
      const result = await response.json();
      setDeletionSuccess(result.message);
      setDeletionError(null);

      // Logout after account deletion
      localStorage.removeItem("authToken");
      navigate("/login");
    } catch (error) {
      setDeletionError("Error deleting account. Please try again.");
      setDeletionSuccess("");
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="profile-container">
      <h1 className="profile-header">Your Profile</h1>
      {success && <p className="success-message">{success}</p>}
      {error && <p className="error-message">{error}</p>}
      <form>
        <div className="form-group">
          <label htmlFor="firstname">First Name:</label>
          <input
            type="text"
            id="firstname"
            name="firstname"
            value={profileData?.firstname || ""}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="lastname">Last Name:</label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            value={profileData?.lastname || ""}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email (Read-only):</label>
          <input
            type="text"
            id="email"
            name="email"
            value={profileData?.email || ""}
            readOnly
          />
        </div>
        <div className="button-container">
          <button type="button" className="button" onClick={handleSave}>
            Update
          </button>
        </div>
      </form>
      {/* Delete Account Section */}
      <div className="delete-account-container">
        <h2>Delete Your Account</h2>
        {deletionSuccess && <p className="success-message">{deletionSuccess}</p>}
        {deletionError && <p className="error-message">{deletionError}</p>}
        <button
          className="button delete-account-button"
          onClick={handleDeleteAccount}
        >
          Delete Account
        </button>
        <p className="warning-text">
          Warning: Deleting your account is permanent and cannot be undone.
        </p>
      </div>
    </div>
  );
};

export default Profile;
