  { type: "dietary", name: "Sustainable", emoji: "ðŸ’š" },
  { type: "dietary", name: "Halal", emoji: "ðŸ•Œ" },
  { type: "dietary", name: "Kosher", emoji: "âœ¡ï¸" },
  { type: "dietary", name: "Hindu Veg", emoji: "ðŸ›•" },
  { type: "dietary", name: "Buddhist Veg", emoji: "â˜¸ï¸" },
  ]

  const handleCheckboxChange = (category, value) => {
  setSelectedOptions(prevState => {
    const newCategory = prevState[category].includes(value)
      ? prevState[category].filter(item => item !== value)
      : [...prevState[category], value];

    // Update the relevant state (allergies or dietaryPreferences)
    if (category === "allergies") {
      setAllergies(newCategory); // Update the allergies state
    } else if (category === "dietary_preferences") {
      setDietaryPreferences(newCategory); // Update the dietaryPreferences state
    }

    return {
      ...prevState,
      [category]: newCategory
    };
  });
};

  useEffect(() => {
    const fetchProfileData = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const response = await fetch(`${baseURL}/profile`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Unauthorized");
        }

        const result = await response.json();
        setProfileData(result);
        setSelectedOptions({
        allergies: result.allergies || [],
        dietary_preferences: result.dietary_preferences || [],
      });

      } catch (error) {
        setError("You are not authorized to access this page.");
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };

    fetchProfileData();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    const token = localStorage.getItem("authToken");

    try {
      const response = await fetch(`${baseURL}/profile`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(profileData),
      });

      if (!response.ok) {
        throw new Error("Failed to update profile");
      }

      const result = await response.json();
      setSuccess(result.message);
      setError(null);
      // Update the user's first name in the Topbar
      if (profileData.firstname) {
        updateFirstName(profileData.firstname);
      }

    } catch (error) {
      setError("Error updating profile. Please try again.");
      setSuccess("");
    }
  };

  // Handle Account Deletion
  const handleDeleteAccount = async () => {
    const token = localStorage.getItem("authToken");

    try {
      const response = await fetch(`${baseURL}/delete_account`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete account");
      }

      // Success message
      const result = await response.json();
      setDeletionSuccess(result.message);
      setDeletionError(null);

      // Logout after account deletion
      localStorage.removeItem("authToken");
      navigate("/login");
    } catch (error) {
      setDeletionError("Error deleting account. Please try again.");
      setDeletionSuccess("");
    }
  };

  const handleShowDeleteConfirmation = () => {
    setShowDeleteConfirmation(true);
  };

  const handleCancelDelete = () => {
    setShowDeleteConfirmation(false);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="profile-container">
      <h1 className="profile-header">Your Profile</h1>
      {success && <p className="success-message">{success}</p>}
      {error && <p className="error-message">{error}</p>}
      <form>
        <div className="form-group">
          <label htmlFor="firstname">First Name:</label>
          <input
              type="text"
              id="firstname"
              name="firstname"
              value={profileData?.firstname || ""}
              onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="lastname">Last Name:</label>
          <input
              type="text"
              id="lastname"
              name="lastname"
              value={profileData?.lastname || ""}
              onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email (Read-only):</label>
          <input
              type="text"
              id="email"
              name="email"
              value={profileData?.email || ""}
              readOnly
          />
        </div>
        <div className="form-group">
          <label>Country:</label>
          <select
              className="input-field select-field"
              value={profileData?.country || ""}
              onChange={(e) => setCountry(e.target.value)}
              required
          >
            <option value="">Select your country</option>
            {countries.map((c) => (
                <option key={c} value={c}>
                  {c}
                </option>
            ))}
          </select>
        </div>
        <div className="form-group checkbox-group">
          <label className="checkbox-label">Allergies (optional):</label>
          <div className="checkbox-options">
            {options
                .filter(option => option.type === "allergy")
                .map(option => (
                    <label key={option.name} className="checkbox-item">
                      <input
                          type="checkbox"
                          value={option.name}
                          checked={selectedOptions.allergies.includes(option.name)}
                          onChange={() => handleCheckboxChange("allergies", option.name)}
                      />
                      <span className="custom-checkbox"></span>
                      <span className="emoji">{option.emoji}</span> {option.name}
                    </label>
                ))}
          </div>
        </div>
        <div className="form-group checkbox-group">
          <label className="checkbox-label">Dietary Requirements (optional):</label>
          <div className="checkbox-options">
            {options
                .filter(option => option.type === "dietary")
                .map(option => (
                    <label key={option.name} className="checkbox-item">
                      <input
                          type="checkbox"
                          value={option.name}
                          checked={selectedOptions.dietary_preferences.includes(option.name)}
                          onChange={() => handleCheckboxChange("dietary_preferences", option.name)}
                      />
                      <span className="custom-checkbox"></span>
                      <span className="emoji">{option.emoji}</span> {option.name}
                    </label>
                ))}
          </div>
        </div>
        <div className="step-container">
          <div className="number-input">
            <button
                type="button"
                onClick={() => setDays(days > 1 ? days - 1 : 1)}
            >
              -
            </button>
            <input
                type="number"
                value={profileData?.plan_days || 1}
                onChange={(e) => setDays(Math.max(1, e.target.value))}
                min="1"
                max="7"
                required
            />
            <button
                type="button"
                onClick={() => setDays(days < 7 ? days + 1 : 7)}
            >
              +
            </button>
          </div>
        </div>
        <div className="button-container">
          <button type="button" className="button" onClick={handleSave}>
            Update
          </button>
        </div>
      </form>
      {/* Delete Account Section */}
      <div className="delete-account-container">
        <h2>Delete Your Account</h2>
        {deletionSuccess && <p className="success-message">{deletionSuccess}</p>}
        {deletionError && <p className="error-message">{deletionError}</p>}
        <button
            className="button delete-account-button"
            onClick={handleShowDeleteConfirmation}
        >
          Delete Account
        </button>
        <p className="warning-text">
          Warning: Deleting your account is permanent and cannot be undone.
        </p>
      </div>
      {/* Confirmation Pop-up */}
      {showDeleteConfirmation && (
          <div className="confirmation-popup">
            <div className="confirmation-content">
              <h3>Are you sure you want to delete your account?</h3>
              <p>This action cannot be undone.</p>
              <div className="confirmation-buttons">
                <button className="button confirm-button" onClick={handleDeleteAccount}>
                  Yes, Delete Account
                </button>
                <button className="button cancel-button" onClick={handleCancelDelete}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
      )}

    </div>
  );
};

export default Profile;
