import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import { jwtDecode } from "jwt-decode";  // Correct import
import './Layout.css';

function Layout() {
  const [userFirstName, setUserFirstName] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (token) {
      try {
        // Decode the JWT token
        const decodedToken = jwtDecode(token);
        // Assuming the first name is stored in the "firstname" field of the JWT payload
        setUserFirstName(decodedToken.firstname);
      } catch (error) {
        console.error("Error decoding token:", error);
        // Optionally handle errors like redirecting to login if decoding fails
      }
    }
  }, []);

  return (
    <div className="layout-container">
      <div className="sidebar">
        {/* Sidebar links */}
        <ul>
          <li>
            <NavLink to="/dashboard" activeClassName="active-link">Dashboard</NavLink>
          </li>
          <li>
            <NavLink to="/analytics" activeClassName="active-link">Analytics</NavLink>
          </li>
          <li>
            <NavLink to="/settings" activeClassName="active-link">Settings</NavLink>
          </li>
        </ul>
      </div>

      <div className="main-content">
        {/* Topbar with Profile Info */}
        <header className="topbar">
          <div className="topbar-left">
            <span>My SaaS App</span>
          </div>
          <div className="topbar-right">
            <div className="profile">
              {/* Display the user's first name, or fallback to "John Doe" */}
              <span>{userFirstName ? userFirstName : "John Doe"}</span>
              <img src="profile.jpg" alt="Profile" />
              <div className="dropdown">
                <ul>
                  <li><a href="/profile">Profile</a></li>
                  <li><a href="/logout">Logout</a></li>
                </ul>
              </div>
            </div>
          </div>
        </header>

        {/* The Outlet component renders the child route's content */}
        <div className="content">
          <Outlet />
        </div>
      </div>
    </div>
  );
}

export default Layout;
